class ScreenView(Ui_MainWindow):
    def __init__(self):

        super().__init__()
        self.ui = Ui_MainWindow()
        self.ui.setupUi(MainWindow)
        self.ui.searchButton.clicked.connect(self.search_button)
        self.ui.mListWidget.clicked.connect(self.movieSelection)



    def search_button(self) -> None:
        self.ui.mListWidget.clear()
        self.idWidgetList = []
        self.titleList = []
        self.total_ids = []
        self.rec_ids = []
        self.query = self.ui.aLineEdit.text().rstrip(' ')

# # Creating Actor Query Instance
        if self.query == '':
            print('No Type')
        else:
            print('Typed')
            if ' ' in self.query:
                segment= self.query.split(' ')
                self.act = ActorAPI(segment[0],segment[1])
                self.actorRequest = self.act.make_request()
            else:
                self.act = ActorAPI(segmentOne=self.query)
                self.actorRequest = self.act.make_request()

 # # Creating request for actor Movie Credits (ids)
        self.mc = MovieCredits(self.actorRequest['results'][0]['id'])
        self.mc = self.mc.make_request()
        for i in range(0,len(self.mc['cast'])):
            self.rec_ids.append(self.mc['cast'][i]['id'])
            self.titleList.append(self.mc['cast'][i]['title'])
        self.ui.mListWidget.addItems(self.titleList[:10])
        
# # Recommend (first) movies based on actor's previous movie credits
    def movieSelection(self) -> None:
        setTextList=[]
        title = []
        recommend = ReccomendationAPI(self.rec_ids[0])
        self.recommend = recommend.make_request()
        for i in range(0,10):
            setTextList.append([self.recommend['results'][i]['title'],self.recommend['results'][i]['id']])
        selected_id = self.ui.mListWidget.currentRow()
        print(f' Type: {type(selected_id)}  and Number: {selected_id}')
        for item in setTextList:
            title.append(item[0])
        self.ui.rListWidget.clear()
        self.ui.rListWidget.addItems(title)
        self.movieSelected = self.mrecomend(setTextList[selected_id][1])

## Display recommended Movie Titles
    def mrecomend(self,selected_id) -> None:
        self.ui.rListWidget.clear()
        recommend = ReccomendationAPI(selected_id)
        self.recommend = recommend.make_request()
        view = []
        for i in range(0,len(self.recommend['results'])):
            view.append(f'{self.recommend['results'][i]['title']}')
        self.ui.rListWidget.addItems(view)


if __name__ == "__main__":
    app = QtWidgets.QApplication(sys.argv)
    MainWindow = QtWidgets.QMainWindow()
    sc = ScreenView()
    MainWindow.show()
    sys.exit(app.exec_())

