class ScreenView(Ui_MainWindow):
    def __init__(self):
        self.clicked = 0
        self.totalPages = 0

        super().__init__()
        self.ui = Ui_MainWindow()
        self.ui.setupUi(MainWindow)
        self.ui.nextButton.clicked.connect(self.next_button_clicked)
        self.ui.previousButton.clicked.connect(self.previous_button_clicked)
        self.ui.searchButton.clicked.connect(self.search_button)
        self.ui.horizontalSlider.setValue(0)

    def search_button(self) -> None:
        # self.ui.listWidget.clear()
        self.pBp = self.ui.popularButton.isChecked()# add to permanent
        self.tRb = self.ui.topratedButton.isChecked()# add to permanent
        self.uCb = self.ui.upcomingButton.isChecked()# add to permanent        
        self.rating = self.ui.horizontalSlider.value()
        if not True in [self.pBp,self.uCb,self.tRb]:
            self.ui.listWidget.clear()
            self.ui.listWidget.addItem('A Selection must be made')
            return
        tag = {self.pBp:'p',self.tRb:'t',self.uCb:'u'} # genre tags
# Create API object to pass to Genres Class
        self.gObj = Genres()
        self.catID = self.gObj.choice(self.ui.comboBox.currentText()) 
        self.api = CreateAPI(tag)
        try:
            _dict = self.api.obj
            if _dict['page'] == 1:
                self.ui.listWidget.clear()
            self.endRange = len(_dict['results'])-1
            try:
                self.ui.listWidget.addItem(f'PAGE : {_dict['page']} \n')
                print('\n')
                for i in range(0,self.endRange):
                    if self.catID in _dict['results'][i]['genre_ids'] and _dict['results'][i]['vote_average'] >= self.rating:
                        _title = f'Title: {_dict['results'][i]['title']}'.format()
                        _overview = f'Description: {_dict['results'][i]['overview']}'
                        _vote_average = f'Vote Average Rating: {_dict['results'][i]['vote_average']}'
                        _fill = '\n'
                        _view = [_title,_overview,_vote_average,_fill]
                        self.ui.listWidget.addItems(_view)
            except KeyError as key:
                print('Something went wrong')
            self.totalPages = _dict['total_pages']
        except ValueError as ve:
            print('Page not found')
        except AttributeError as ae:
            print('No Attributes')
        
    def next_button_clicked(self) -> None:
        self.api.clicked +=1
        self.ui.listWidget.clear()
        self.rating = self.ui.horizontalSlider.value()
        if self.api.clicked < self.totalPages:
            _dict = self.api.make_request(self.api.endpoint,page=self.api.clicked)
            self.ui.listWidget.addItem(f'PAGE : {_dict['page']} \n')
            print('\n')
            try:
                for i in range(0,self.endRange):
                    if self.catID in _dict['results'][i]['genre_ids'] and _dict['results'][i]['vote_average'] >= self.rating:
                        _title = f'Title: {_dict['results'][i]['title']}'.format()
                        _overview = f'Description: {_dict['results'][i]['overview']}'
                        _vote_average = f'Vote Average Rating: {_dict['results'][i]['vote_average']}'
                        _fill = '\n'
                        _view = [_title,_overview,_vote_average,_fill]
                        self.ui.listWidget.addItems(_view)
            except KeyError as key:
                print('Something went wrong')
        else:
            print("Can't go any further")

    def previous_button_clicked(self) -> None:
        self.ui.listWidget.clear()
        self.api.clicked -=1
        self.rating = self.ui.horizontalSlider.value()
        if self.api.clicked <= 1:
            self.api.clicked = 1
            _dict = self.api.obj
            self.ui.listWidget.addItem(f'PAGE : {_dict['page']} \n')
            print(f'PAGE: ',_dict['page'])
            print('\n')
            for i in range(0,self.endRange):
                if self.catID in _dict['results'][i]['genre_ids'] and _dict['results'][i]['vote_average'] >= self.rating:
                    _title = f'Title: {_dict['results'][i]['title']}'.format()
                    _overview = f'Description: {_dict['results'][i]['overview']}'
                    _vote_average = f'Vote Average Rating: {_dict['results'][i]['vote_average']}'
                    _fill = '\n'
                    _view = [_title,_overview,_vote_average,_fill]
                    self.ui.listWidget.addItems(_view)
            print('Can\'t go back further')
        else:    
            _dict = self.api.make_request(self.api.endpoint,page=self.api.clicked)
            try:
                self.ui.listWidget.addItem(f'PAGE : {_dict['page']} \n')
                for i in range(0,self.endRange):
                    if self.catID in _dict['results'][i]['genre_ids'] and _dict['results'][i]['vote_average'] >= self.rating:
                        _title = f'Title: {_dict['results'][i]['title']}'.format()
                        # _genre_id = f'Genre Id: {_dict['results'][i]['genre_ids']}'.format()
                        _overview = f'Description: {_dict['results'][i]['overview']}'
                        _vote_average = f'Vote Average Rating: {_dict['results'][i]['vote_average']}'
                        _fill = '\n'
                        _page = f'PAGE : {self.api.clicked}'
                        _view = [_title,_overview,_vote_average,_fill]
                        self.ui.listWidget.addItems(_view)
            except KeyError as key:
                print('Something went wrong')


